import React from 'react';

function WeatherDisplay(props) {
  const forecast = props.weather
  const iconURL = `owf owf-${forecast.cod} owf-5x`
  //obtains day of week
  const dayOfWeek = (date) =>{
    /*
    the Date constructor uses the UNIX Timestamp with milliseconds, but the API call uses it without.
    So we multiply the given timestamp date by 1000 to add the milliseconds.
    */
    const index = new Date(date * 1000).getDay()
    console.log(new Date(date * 1000))
    return ["Sunday","Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"][index]
  }
  return (
    <div>
      <h1>{forecast.name}, {forecast.sys.country} </h1>
      <h3>{dayOfWeek(forecast.dt)}</h3>
      <i className={iconURL}></i>
      <h3>{Math.round(forecast.main.temp)} °C | {Math.round(forecast.main.temp * 9/5 + 32)} °F </h3>
      <p>{forecast.weather[0].description} </p>
    </div>
  );
}

export default WeatherDisplay;
