      <div className="columns is-6">
        {/* <DayForecast forecast={weather} />
        <DayForecast forecast={weather} />
        <DayForecast forecast={weather} />
        <DayForecast forecast={weather} />
        <DayForecast forecast={weather} /> */}
      </div>
    </div>
  );
}

export default WeatherDisplay;
