import React, {useState, useEffect} from 'react';
import './App.css';

function App() {
  const [weather, setWeather] = useState();

  useEffect(() => {
    fetch('http://api.openweathermap.org/data/2.5/weather?id=3492908&appid=f8cccb4dbfc14738615a10fa33bfdf42')
    .then(res => res.json()).then(res => setWeather(res));
  }, [])

  const weatherData = () => {
    if (weather != null){
      Object.keys(weather).map((test, index) =>{
        {alert(test)}
      })
    }
    else{
      return <p>no data</p>
    }
  }

  return (
    <div>
      {weatherData()}
    </div>
  );
}

export default App;
