import React, {useState, useEffect} from 'react';
import './App.css';
import WeatherDisplay from './WeatherDisplay'


function App() {
  const [apiData, setApiData] = useState();

  useEffect(() => {
    fetch('http://api.openweathermap.org/data/2.5/weather?id=3492908&appid=f8cccb4dbfc14738615a10fa33bfdf42&units=metric')
    .then(res => res.json()).then(res => setApiData(res));
  }, [])


  return (
    <div className="App"> 
      <section class="hero is-primary">
        <div class="hero-body">
          <div class="container">
            <h1 class="title" style={{fontSize:50}}>
              Weather Forecasting App Made in React 
            </h1>
            <h2 class="subtitle">
              made by <a href="https://github.com/mosesdiaz" target="_blank" style={{textDecoration: underline}}>Moises Diaz</a>
            </h2>
          </div>
        </div>
      </section>
      {/*Renders when api call sets weather State */}
      {apiData != null ? <WeatherDisplay weather={apiData}/> : <h3>loading...</h3>}

    </div>
  );
}

export default App;
