  const [weather, setWeather] = useState({
    "coord": {
       "lon": -69.99,
       "lat": 18.5
    },
    "weather": [
       {
          "id": 804,
          "main": "Clouds",
          "description": "overcast clouds",
          "icon": "04n"
       }
    ],
    "base": "stations",
    "main": {
       "temp": 297.64,
       "feels_like": 301.11,
       "temp_min": 297.64,
       "temp_max": 297.64,
       "pressure": 1017,
       "humidity": 78,
       "sea_level": 1017,
       "grnd_level": 1010
    },
    "wind": {
       "speed": 0.59,
       "deg": 20
    },
    "clouds": {
       "all": 100
    },
    "dt": 1589164628,
    "sys": {
       "country": "DO",
       "sunrise": 1589105281,
       "sunset": 1589151884
    },
    "timezone": -14400,
    "id": 3492908,
    "name": "Santo Domingo",
    "cod": 200
 });

  // useEffect(() => {
