  // useEffect(() =>{
  //   setWeather()
  // })

  //  prueba = () => {
  //   return weather.map((read) => <li>{read}</li>)
  // }

  return (
    <div>
      <ul>
        {/*Renders when api call sets weather State */}
        {weather != null ? <WeatherDisplay weather={weather}/> : <h3>loading...</h3>}
      </ul>
    </div>
  );
}

export default App;
