import React, {useState, useEffect} from 'react';
import './App.css';

function App() {
  const [weather, setWeather] = useState();

  useEffect(() => {
    fetch('api.openweathermap.org/data/2.5/weather?q=3492908&appid=f8cccb4dbfc14738615a10fa33bfdf42')
    .then(res => res.json()).then(res => setWeather(res));
  }, [])

  return (
    <div>
      <h1>{weather}</h1>
    </div>
  );
}

export default App;
