import React, {useState, useEffect} from 'react';
import './App.css';
import WeatherDisplay from './WeatherDisplay'


function App() {
  const [weather, setWeather] = useState();

  useEffect(() => {
    fetch('http://api.openweathermap.org/data/2.5/weather?id=3492908&appid=f8cccb4dbfc14738615a10fa33bfdf42')
    .then(res => res.json()).then(res => setWeather(res));
  }, [])

  // useEffect(() =>{
  //   setWeather()
  // })

  //  prueba = () => {
  //   return weather.map((read) => <li>{read}</li>)
  // }

  return (
    <div>
      <ul>
        {/*Renders when api call sets weather State */}
        {weather != null ? <WeatherDisplay weather={weather}/> : <h3>loading...</h3>}
      </ul>
    </div>
  );
}

export default App;
