import React, {useState, useEffect} from 'react';
import './App.css';
import WeatherDisplay from './WeatherDisplay'


function App() {

  const [apiData, setApiData] = useState({"coord":{"lon":-69.99,"lat":18.5},"weather":[{"id":800,"main":"Clear","description":"clear sky","icon":"01d"}],"base":"stations","main":{"temp":28.53,"feels_like":34.51,"temp_min":28.33,"temp_max":28.89,"pressure":1016,"humidity":91},"wind":{"speed":2.41,"deg":167},"clouds":{"all":3},"dt":1589237983,"sys":{"type":3,"id":197946,"country":"DO","sunrise":1589191657,"sunset":1589238307},"timezone":-14400,"id":3492908,"name":"Santo Domingo","cod":200});

  //for 5 day forecast:
  //http://api.openweathermap.org/data/2.5/weather?id=3492908&appid=f8cccb4dbfc14738615a10fa33bfdf42&units=metric
  useEffect(() => {
    fetch('http://api.openweathermap.org/data/2.5/forecast?id=3492908&appid=f8cccb4dbfc14738615a10fa33bfdf42&units=metric')
    .then(res => res.json())
    .then(res => {
      //const result = res.list.filter(reading => reading.dt_text.includes("06:00:00"))
      console.log(res.list.filter(r => r.sys.pod.includes('d')))
    })
  }, [])


  return (
    <div className="App"> 
      <section className="hero is-primary">
        <div className="hero-body">
          <div className="container">
            <h1 className="title" style={{fontSize:50}}>
              Weather Forecasting App Made in React 
            </h1>
            <h2 className="subtitle">
              made by <a href="https://github.com/mosesdiaz" target="_blank" style={{textDecoration: "underline"}}>Moises Diaz</a>
            </h2>
          </div>
        </div>
      </section>
      {/*Renders when api call sets weather State */}
      {apiData != null ? <WeatherDisplay weather={apiData}/> : <h3>loading...</h3>}

    </div>
  );
}

export default App;
